package Slding_Window;

import java.util.*;

public class findAllAnagramInAllStringHashMap {
    // Two words are anagrams of each other if the count of every character in both the words are same.
    private static boolean isAnagram(Map<Character, Integer> word1CharCount, Map<Character, Integer> word2CharCount) {
        if (word1CharCount.size() != word2CharCount.size()) {
            return false;
        }

        if (word1CharCount.equals(word2CharCount)) {
            return true;
        } else {
            return false;
        }


    }

    private static List<Integer> countOccurrenceOfAnagram(String s, String p) {

        // write your code here
        HashMap<Character, Integer> smap = new HashMap<>();
        HashMap<Character, Integer> pmap = new HashMap<>();

        List<Integer> result = new ArrayList<>();

        for (int i = 0; i < p.length(); i++) {
            char ch = p.charAt(i);
            int ofreq = pmap.getOrDefault(ch, 0);
            pmap.put(ch, ofreq + 1);
        }

        for (int i = 0; i < p.length(); i++) {
            char ch = s.charAt(i);
            int ofreq = smap.getOrDefault(ch, 0);
            smap.put(ch, ofreq + 1);
        }

        int j = 0;
        int i = p.length();

        while (i < s.length()) {
            if (isAnagram(pmap, smap) == true) {
                result.add(i - p.length());
            }

            char cha = s.charAt(i);
            smap.put(cha, smap.getOrDefault(cha, 0) + 1);

            char chr = s.charAt(j);

            if (smap.get(chr) == 1) {
                smap.remove(chr);
            } else {
                smap.put(chr, smap.get(chr) - 1);
            }

            i++;
            j++;
        }

        if (isAnagram(pmap, smap) == true) {
            result.add(i - p.length());
        }

        return result;
    }

    public static void main(String[] args) {
        String s = new String("");
        String p = new String("");

        // countOccurrenceOfAnagram(s,p);
        // int count=countOccurrenceOfAnagram(s,p);
        List<Integer> result = countOccurrenceOfAnagram(s, p);
        //System.out.println(count);
        System.out.println(result);


    }
}
